DROP TABLE toy_board CASCADE CONSTRAINTS;CREATE TABLE toy_board (	ID	number		NOT NULL,	title	varchar2(500)		NOT NULL,	content	varchar2(4000)		NOT NULL,	user_id	varchar2(50)		NOT NULL,	user_pw	varchar2(200)		NOT NULL,	create_date	timestamp		NOT NULL,	modi_date	timestamp		,	del_flag	varchar2(5)		NOT NULL,	category	number	        NOT	NULL,    view_cnt    number  DEFAULT 0    );ALTER TABLE toy_board ADD CONSTRAINT PK_toy_BOARD PRIMARY KEY (ID);--카테고리 일반글 10 질문글 20 공지글 50 전체공지 60 CREATE TABLE CATEGORY (	CATEGORY_ID	NUMBER		NOT NULL,	CATEGORY_NAME	VARCHAR2(200)		NULL);ALTER TABLE CATEGORY ADD CONSTRAINT PK_CATEGORY PRIMARY KEY (CATEGORY_ID);--댓글 테이블CREATE TABLE board_comment (	COMMENT_ID	NUMBER	NOT NULL,	REF_ID	number	NOT NULL,	USER_ID	VARCHAR2(50)	NULL,	USER_PW	VARCHAR2(100)	NULL,	CONTEXT	VARCHAR2(2000)	NULL,	REG_DATE	TIMESTAMP	NULL,	COMMENT_STEP	NUMBER	NULL,	COMM_REF_ID	NUMBER	NULL);ALTER TABLE board_comment ADD CONSTRAINT PK_BOARD_COMMENT PRIMARY KEY (	COMMENT_ID);--카테고리 테이블 내용      INSERT INTO CATEGORY (CATEGORY_ID, CATEGORY_NAME) VALUES (10, '일반');INSERT INTO CATEGORY (CATEGORY_ID, CATEGORY_NAME) VALUES (50, '공지');INSERT INTO CATEGORY (CATEGORY_ID, CATEGORY_NAME) VALUES (60, '전체공지');INSERT INTO CATEGORY (CATEGORY_ID, CATEGORY_NAME) VALUES (20, '질문');CREATE SEQUENCE BOARD_SEQ       INCREMENT BY 1       START WITH 1       MINVALUE 1       NOCYCLE       NOCACHE       NOORDER;              CREATE SEQUENCE COMMENT_SEQ       INCREMENT BY 1       START WITH 1       MINVALUE 1       NOCYCLE       NOCACHE       NOORDER;